<?phpinclude '../inc/conexion.php';//iniciar sesión para el  usuariosession_start();//función para almacenar datos de sesiónfunction iniciarSesion($idusuario,$nomusuario,$rolusuario){	session_start();			$_SESSION['Id'] = session_id();	$_SESSION['Usuario']=$idusuario;	$_SESSION['NombresUsuario']=$nomusuario;	$_SESSION['CodRoll']=$rolusuario;			//llamado a pagina inicio.php	echo "<script language=javascript>";	echo "window.location='../inicio.php';";	echo "</script>";				}	//recibe datos usuario y clave	$usuario = $_POST['txtusuario'];$clave = md5($_POST['txtclave']);	//Validar que el usuario y contraseña sean validos (registrados en la base de datos)	$link = Conectarse();	    $result=mysql_query("SELECT idusuario, nombreusuario, rolusuario FROM usuarios WHERE idusuario='".$usuario."' AND claveusuario='".$clave."' AND estado='A'",$link);    $row=mysql_fetch_row($result);    if (mysql_num_rows($result)==1)	{		//usuario y contraseña existen        //envia datos de usuario para almacenar en sesión		iniciarSesion($row[0], $row[1], $row[2]);	}	else	{//usuario o contraseña no existen. vuelve a pagina de inicio (index.php)            echo "<script language=javascript>";			echo "alert('El nombre de usuario o la contraseña no existe!');";			echo "window.location='../';";			echo "</script>";	}	mysql_free_result($result);?>